let canvas = document.querySelector("canvas");
let context = canvas.getContext("2d");
let width = window.screen.width;
let height = window.screen.height;
showCanvas(false);


let image = new Image();
image.src = "data:image/jpeg;base64,";
image.onload = main;


let id = 0;
let speed = 10;
let step = 1;
let doX = true;
let doY = false;

let x = 0;
let y = 0;
function main() {
	clearInterval(id);
	id = setInterval(e => {
		context.clearRect(0, 0, canvas.width, canvas.height);
		if(doX) x+=canvas.width/width*step;
		if(doY) y+=canvas.width/width*step;
		draw(x%canvas.width, y%canvas.height);
		if(doX)	
			draw(x%canvas.width-canvas.width, y%canvas.height);
		if(doY)
			draw(x%canvas.width, y%canvas.height-canvas.height);
		if(doX && doY)
			draw(x%canvas.width-canvas.width, y%canvas.height-canvas.height);
	}, speed);
}

function draw(nx, ny, flipped = false) {
	context.drawImage(image, 0, 0, image.width, image.height, nx, ny, canvas.width, canvas.height);
}

document.querySelector("#x").addEventListener("click", e=> { 
	doX = e.path[0].checked;
	if(!e.path[0].checked)
		x = 0;
	main();
});

document.querySelector("#y").addEventListener("click", e=> { 
	doY = e.path[0].checked;
	if(!e.path[0].checked)
		y = 0;
	main();
});


document.querySelector("input[type='range']").addEventListener("input", e=> {
	let vals = values(e.path[0].value);
	document.querySelector("#display").innerHTML = "speed: " + vals[0] + "ms, steps: "+vals[1];
	speed = vals[0];
	step = vals[1];
	main();
});
document.querySelector("input[type='range']").addEventListener("change", e=> {
	let vals = values(e.path[0].value);
	speed = vals[0];
	step = vals[1];
	main();
});

document.querySelector("input[type='file']").addEventListener("change", e=> {
	let newImg = new Image();
    var fr = new FileReader();
	fr.onload = function () {
		newImg.src = fr.result;
		newImg.onload = e => {
			image = newImg;
			main();
		};
	}
	fr.readAsDataURL(e.path[0].files[0]);
});

function values(value) {
	if(value < 1) {
		return [1, value==0? 1: Math.abs(value)];
	}
	return [value, 1];
}

function showCanvas(show) {
	if(show) {
		canvas.width = width;
		canvas.height = height;
		canvas.style.cssText = "position: absolute; top: 0px; left: 0px;";
		document.body.style.cursor = "none"
	} else {
		canvas.width = width;
		canvas.height = height;
		canvas.style.cssText = "border:1px solid #000000;";
		document.body.style.cursor = "";
	}
}

window.addEventListener('resize', e => {
	width = e.target.outerWidth+16	;
	height = e.target.outerHeight+16;
	if(document.fullscreenElement) {
		canvas.width = width;
		canvas.height = height;
	}
});

window.addEventListener("keydown", e => {
	if(e.code == "KeyF") {
		if (!document.fullscreenElement) {
			document.documentElement.requestFullscreen();
			showCanvas(true);
		} else {
			if (document.exitFullscreen) {
				document.exitFullscreen();
				showCanvas(false);				
			}
		}
	}
});